import React, { useEffect, useState, useContext } from 'react';
import { useParams } from 'react-router-dom';
import api from '../../utils/api';
// Importe le formulaire de création d'avis
import ReviewForm from '../../components/product/ReviewForm';
import { CartContext } from '../../context/CartContext';

const ProduitDetail = () => {
  const { id } = useParams();
  const [produit, setProduit] = useState(null);
  const [reviews, setReviews] = useState([]);

  // Fonction pour recharger les avis
  const fetchReviews = () => {
    api.get(`/products/${id}/reviews`) // Suppose une route pour les avis d'un produit
       .then(res => setReviews(res.data))
       .catch(err => console.error(err));
  };

  useEffect(() => {
    api.get(`/products/${id}`)
       .then(res => setProduit(res.data))
       .catch(err => setProduit(null));
    fetchReviews();
  }, [id]);

  if (!produit) return <p>Produit non trouvé.</p>;

  return (
    <div>
      <h2>{produit.name}</h2>
      {/* ... autres détails du produit ... */}
      
      <h3>Avis des clients</h3>
      <ul>
        {reviews.map(review => (
          <li key={review.id}>
            <strong>Note : {review.rating}/5</strong>
            <p>{review.comment}</p>
          </li>
        ))}
      </ul>

      {/* Affiche le formulaire d'avis si l'utilisateur est connecté */}
      <ReviewForm productId={id} onReviewSubmit={fetchReviews} />
    </div>
  );
};

export default ProduitDetail;
